document.addEventListener("DOMContentLoaded", function () {
  const countryFilter = document.getElementById("countryFilter");
  const yearFilter = document.getElementById("yearFilter");
  const rows = document.querySelectorAll("#countryTable tr");

  function filterTable() {
    const selectedCountry = countryFilter.value;
    const selectedYear = yearFilter.value;

    rows.forEach(row => {
      const rowCountry = row.dataset.country;
      const rowYear = row.dataset.year;

      const matchCountry = (selectedCountry === "all" || rowCountry === selectedCountry);
      const matchYear = (selectedYear === "all" || rowYear === selectedYear);

      row.style.display = (matchCountry && matchYear) ? "" : "none";
    });
  }

  countryFilter.addEventListener("change", filterTable);
  yearFilter.addEventListener("change", filterTable);
});

