let filterButton = document.querySelector("button.filters-cta");

const presets = [
	"filter-preset-1",
	"filter-preset-2",
	"filter-preset-3",
	"filter-preset-4",
	"filter-preset-5",
	"filter-preset-6",
	"filter-preset-7",
	"filter-preset-8",
	"filter-preset-9",
	"filter-preset-10",
	"filter-preset-11",
	"filter-preset-12",
	"filter-preset-13",
	"filter-preset-14",
	"filter-preset-15",
	"filter-preset-16"
];




document.addEventListener("DOMContentLoaded", function () {
  const countryFilter = document.getElementById("countryFilter");
  const yearFilter = document.getElementById("yearFilter");
  const rows = document.querySelectorAll("#countryTable tr");

  function filterTable() {
    const selectedCountry = countryFilter.value;
    const selectedYear = yearFilter.value;

    rows.forEach(row => {
      const rowCountry = row.dataset.country;
      const rowYear = row.dataset.year;

      const matchCountry = (selectedCountry === "all" || rowCountry === selectedCountry);
      const matchYear = (selectedYear === "all" || rowYear === selectedYear);

      row.style.display = (matchCountry && matchYear) ? "" : "none";
    });
  }

  countryFilter.addEventListener("change", filterTable);
  yearFilter.addEventListener("change", filterTable);
});


/* eslint-disable no-alert */
const els = {
  searchInput: document.getElementById("searchInput"),
  onlySelected: document.getElementById("onlySelected"),
  grid: document.getElementById("grid"),
  shownCount: document.getElementById("shownCount"),
  selectedCount: document.getElementById("selectedCount"),
  activeFilters: document.getElementById("activeFilters"),
  emptyState: document.getElementById("emptyState"),

  shareSetBtn: document.getElementById("shareSetBtn"),
  clearSetBtn: document.getElementById("clearSetBtn"),

  accButtons: Array.from(document.querySelectorAll(".accordion")),
  accBodies: {
    year: document.querySelector('[data-body="year"]'),
    competition: document.querySelector('[data-body="competition"]'),
    award: document.querySelector('[data-body="award"]'),
    category: document.querySelector('[data-body="category"]'),
    location: document.querySelector('[data-body="location"]')
  },

  viewer: document.getElementById("viewer"),
  viewerTopTitle: document.getElementById("viewerTopTitle"),
  viewerImg: document.getElementById("viewerImg"),
  viewerTitle: document.getElementById("viewerTitle"),
  viewerPhotographer: document.getElementById("viewerPhotographer"),
  viewerYear: document.getElementById("viewerYear"),
  viewerCompetition: document.getElementById("viewerCompetition"),
  viewerAward: document.getElementById("viewerAward"),
  viewerCategory: document.getElementById("viewerCategory"),
  viewerLocation: document.getElementById("viewerLocation"),
  toggleSelectBtn: document.getElementById("toggleSelectBtn"),
  copyPhotoLinkBtn: document.getElementById("copyPhotoLinkBtn"),
  prevBtn: document.getElementById("prevBtn"),
  nextBtn: document.getElementById("nextBtn"),
  closeBtn: document.getElementById("closeBtn"),

  toast: document.getElementById("toast")
};

const STORAGE_KEY = "gallery_selected_v1";

let PHOTOS = [];
let selected = new Set(loadSelected());
let viewIndex = -1;
let currentResults = [];

const filters = {
  year: new Set(),
  competition: new Set(),
  award: new Set(),
  category: new Set(),
  location: new Set()
};

function toast(msg) {
  els.toast.textContent = msg;
  els.toast.classList.add("show");
  clearTimeout(toast._t);
  toast._t = setTimeout(() => els.toast.classList.remove("show"), 1400);
}

function loadSelected() {
  try {
    const raw = localStorage.getItem(STORAGE_KEY);
    if (!raw) return [];
    const arr = JSON.parse(raw);
    return Array.isArray(arr) ? arr : [];
  } catch {
    return [];
  }
}

function saveSelected() {
  localStorage.setItem(STORAGE_KEY, JSON.stringify(Array.from(selected)));
  els.selectedCount.textContent = String(selected.size);
}

function uniq(arr) {
  return Array.from(new Set(arr)).sort((a, b) => String(a).localeCompare(String(b)));
}

function countBy(arr, key) {
  const m = new Map();
  for (const x of arr) m.set(x[key], (m.get(x[key]) || 0) + 1);
  return m;
}

function buildCheckboxList(targetEl, key, values, countsMap) {
  targetEl.innerHTML = "";
  for (const v of values) {
    const row = document.createElement("label");
    row.className = "check";
    row.innerHTML = `
      <input type="checkbox" />
      <span class="lbl"></span>
      <span class="cnt"></span>
    `;
    row.querySelector(".lbl").textContent = String(v);
    row.querySelector(".cnt").textContent = String(countsMap.get(v) || 0);

    const cb = row.querySelector("input");
    cb.addEventListener("change", () => {
      if (cb.checked) filters[key].add(v);
      else filters[key].delete(v);
      render();
    });

    targetEl.appendChild(row);
  }
}

function activeFilterSummary() {
  const parts = [];
  const add = (k, label) => {
    if (filters[k].size) parts.push(`${label}: ${Array.from(filters[k]).join(", ")}`);
  };
  add("year", "Dates");
  add("competition", "Competitions");
  add("award", "Award");
  add("category", "Categories");
  add("location", "Locations");
  if (els.onlySelected.checked) parts.push("Selected only");
  if (els.searchInput.value.trim()) parts.push(`Search: ‚Äú${els.searchInput.value.trim()}‚Äù`);
  return parts.length ? `Active filters: ${parts.join(" ¬∑ ")}` : "Active filters: none";
}

// AND across groups, OR within group
function passes(photo) {
  const q = els.searchInput.value.trim().toLowerCase();
  if (els.onlySelected.checked && !selected.has(photo.id)) return false;

  const groups = [
    ["year", photo.year],
    ["competition", photo.competition],
    ["award", photo.award],
    ["category", photo.category],
    ["location", photo.location]
  ];

  for (const [k, v] of groups) {
    if (filters[k].size && !filters[k].has(v)) return false;
  }

  if (q) {
    const hay = `${photo.title} ${photo.photographer} ${photo.year} ${photo.competition} ${photo.award} ${photo.category} ${photo.location}`.toLowerCase();
    if (!hay.includes(q)) return false;
  }
  return true;
}

function render() {
  currentResults = PHOTOS.filter(passes);
  els.shownCount.textContent = String(currentResults.length);
  els.activeFilters.textContent = activeFilterSummary();

  els.grid.innerHTML = "";
  els.emptyState.hidden = currentResults.length !== 0;

  for (const p of currentResults) {
    const card = document.createElement("article");
    card.className = "card";
    card.role = "listitem";
    card.dataset.id = p.id;

    const on = selected.has(p.id);

    card.innerHTML = `
      <div class="thumb"><img loading="lazy" src="${p.image}" alt="${escapeHtml(p.title)}"></div>
      <button class="selectBtn" type="button" data-on="${on}">${on ? "‚úì" : "+"}</button>
      <div class="meta">
        <h3>${escapeHtml(p.title)}</h3>
        <p>üì∑ ${escapeHtml(p.photographer)} <span>‚Ä¢</span> üèÜ ${escapeHtml(p.award)}</p>
      </div>
    `;

    const selBtn = card.querySelector(".selectBtn");
    selBtn.addEventListener("click", (e) => {
      e.stopPropagation();
      toggleSelected(p.id);
      selBtn.dataset.on = String(selected.has(p.id));
      selBtn.textContent = selected.has(p.id) ? "‚úì" : "+";
      if (els.onlySelected.checked) render();
    });

    card.addEventListener("click", () => openViewerById(p.id));
    els.grid.appendChild(card);
  }
}

function escapeHtml(s) {
  return String(s)
    .replaceAll("&", "&amp;")
    .replaceAll("<", "&lt;")
    .replaceAll(">", "&gt;")
    .replaceAll('"', "&quot;")
    .replaceAll("'", "&#039;");
}

function toggleSelected(id) {
  if (selected.has(id)) {
    selected.delete(id);
    toast("Removed from your set");
  } else {
    selected.add(id);
    toast("Added to your set");
  }
  saveSelected();
}

function buildShareUrl() {
  const url = new URL(location.href);
  url.searchParams.set("set", Array.from(selected).join(","));
  return url.toString();
}

async function copyText(text) {
  try {
    await navigator.clipboard.writeText(text);
    return true;
  } catch {
    const ta = document.createElement("textarea");
    ta.value = text;
    ta.style.position = "fixed";
    ta.style.left = "-9999px";
    document.body.appendChild(ta);
    ta.select();
    const ok = document.execCommand("copy");
    document.body.removeChild(ta);
    return ok;
  }
}

function applySetFromUrl() {
  const raw = new URL(location.href).searchParams.get("set");
  if (!raw) return;
  const ids = raw.split(",").map(s => s.trim()).filter(Boolean);
  const valid = new Set(PHOTOS.map(p => p.id));
  for (const id of ids) if (valid.has(id)) selected.add(id);
  saveSelected();
}

function openViewerById(id) {
  const idx = currentResults.findIndex(p => p.id === id);
  if (idx < 0) return;
  viewIndex = idx;
  fillViewer(currentResults[viewIndex]);
  els.viewer.showModal();
}

function fillViewer(p) {
  els.viewerTopTitle.textContent = `${p.title} (${p.year})`;
  els.viewerImg.src = p.image;
  els.viewerImg.alt = p.title;

  els.viewerTitle.textContent = p.title;
  els.viewerPhotographer.textContent = p.photographer;
  els.viewerYear.textContent = String(p.year);
  els.viewerCompetition.textContent = p.competition;
  els.viewerAward.textContent = p.award;
  els.viewerCategory.textContent = p.category;
  els.viewerLocation.textContent = p.location;

  const on = selected.has(p.id);
  els.toggleSelectBtn.textContent = on ? "Remove from set" : "Add to set";
  els.toggleSelectBtn.classList.toggle("btn-primary", !on);
}

function stepViewer(dir) {
  if (!currentResults.length) return;
  viewIndex = (viewIndex + dir + currentResults.length) % currentResults.length;
  fillViewer(currentResults[viewIndex]);
}

function setupAccordions() {
  for (const btn of els.accButtons) {
    btn.addEventListener("click", () => {
      const key = btn.dataset.acc;
      const body = els.accBodies[key];
      const expanded = btn.getAttribute("aria-expanded") === "true";
      btn.setAttribute("aria-expanded", String(!expanded));
      body.classList.toggle("is-collapsed", expanded);
    });
  }
}

async function init() {
  const res = await fetch("assets/data.json", { cache: "no-store" });
  PHOTOS = await res.json();

  // Build filter lists with counts
  const maps = {
    year: countBy(PHOTOS, "year"),
    competition: countBy(PHOTOS, "competition"),
    award: countBy(PHOTOS, "award"),
    category: countBy(PHOTOS, "category"),
    location: countBy(PHOTOS, "location")
  };

  buildCheckboxList(els.accBodies.year, "year", uniq(PHOTOS.map(p => p.year)).sort((a,b)=>b-a), maps.year);
  buildCheckboxList(els.accBodies.competition, "competition", uniq(PHOTOS.map(p => p.competition)), maps.competition);
  buildCheckboxList(els.accBodies.award, "award", uniq(PHOTOS.map(p => p.award)), maps.award);
  buildCheckboxList(els.accBodies.category, "category", uniq(PHOTOS.map(p => p.category)), maps.category);
  buildCheckboxList(els.accBodies.location, "location", uniq(PHOTOS.map(p => p.location)), maps.location);

  setupAccordions();

  applySetFromUrl();
  els.selectedCount.textContent = String(selected.size);

  els.searchInput.addEventListener("input", render);
  els.onlySelected.addEventListener("change", render);

  els.shareSetBtn.addEventListener("click", async () => {
    if (selected.size === 0) return alert("Select at least one photo first.");
    const ok = await copyText(buildShareUrl());
    toast(ok ? "Share link copied" : "Could not copy link");
  });

  els.clearSetBtn.addEventListener("click", () => {
    selected.clear();
    saveSelected();
    const url = new URL(location.href);
    url.searchParams.delete("set");
    history.replaceState({}, "", url.toString());
    toast("Selection cleared");
    render();
  });

  els.toggleSelectBtn.addEventListener("click", () => {
    const p = currentResults[viewIndex];
    toggleSelected(p.id);
    fillViewer(p);
    render();
  });

  els.copyPhotoLinkBtn.addEventListener("click", async () => {
    const p = currentResults[viewIndex];
    const url = new URL(location.href);
    url.searchParams.set("photo", p.id);
    const ok = await copyText(url.toString());
    toast(ok ? "Photo link copied" : "Could not copy link");
  });

  els.prevBtn.addEventListener("click", () => stepViewer(-1));
  els.nextBtn.addEventListener("click", () => stepViewer(1));
  els.closeBtn.addEventListener("click", () => els.viewer.close());

  window.addEventListener("keydown", (e) => {
    if (!els.viewer.open) return;
    if (e.key === "Escape") els.viewer.close();
    if (e.key === "ArrowLeft") stepViewer(-1);
    if (e.key === "ArrowRight") stepViewer(1);
  });

  render();
}

init().catch(() => alert("Failed to load assets/data.json. Run via a local server (not by double-clicking index.html)."));
